declare module "@gooddata/goodstrap/lib/Header/Header" {
    export interface IBootstrapResource {
        accountSetting: any;
        current: any;
        profileSetting: any;
        settings: any;
        hostnameBase?: string;
    }

    export interface IBootstrapData {
        bootstrapResource: IBootstrapResource;
    }

    export interface IHeaderMenuOptions {
        activeItem: string;
    }

    export interface IHeaderHelpOptions {
        sessionId: string;
    }

    export interface IHeaderMenu {
        key: string;
        className: string;
        href: string;
        isActive: boolean;
    }

    export interface IHeaderMenuList {
        [index: number]: IHeaderMenu[];
    }

    export default class Header extends React.Component<any, any> {}
    export function generateHeaderMenu(
        bootstrapData: IBootstrapData,
        options: IHeaderMenuOptions,
    ): IHeaderMenuList[];
    export function generateAccountMenu(bootstrapData: IBootstrapData): IHeaderMenu[];
    export function generateHelpMenu(
        bootstrapData: IBootstrapData,
        options: IHeaderHelpOptions,
    ): IHeaderMenu[];
}
