declare module "@gooddata/goodstrap/lib/List/InvertableList" {
    import { IntlShape } from "react-intl";

    export interface IInvertableListState {
        notifyLimitHit: boolean;
    }

    export type IInvertableListItem = any;

    export interface IInvertableListProps {
        className?: string;
        filteredItemsCount: number;
        getItemKey?: (item: IInvertableListItem) => string;
        height: number;
        isInverted?: boolean;
        isLoading?: boolean;
        isLoadingClass: React.ComponentType<any>;
        isMobile?: boolean;
        itemHeight: number;
        items: ReadonlyArray<IInvertableListItem>;
        itemsCount: number;
        limitHitWarningClass?: React.ComponentType<any>;
        listItemClass?: React.ComponentType<any>;
        maxSelectionSize: number;
        noItemsFound?: boolean;
        noItemsFoundClass?: React.ComponentType<any>;
        onRangeChange?: (searchString: string, start: number, end: number) => void;
        onSearch: (searchString: string) => void;
        onSelect?: (selection: IInvertableListItem[], isInverted: boolean) => void;
        searchPlaceholder?: string;
        searchString?: string;
        selection?: IInvertableListItem[];
        showSearchField?: boolean;
        smallSearch?: boolean;
        tagName?: string;
        width?: number;
        actionsAsCheckboxes?: boolean;
        // passed down to MultiSelectList
        selectAllCheckbox?: boolean;
        loadingListItemClass?: React.ComponentType<any>;
        rowItem?: React.ReactElement<any>;
        paging?: boolean;
    }

    export class InvertableList extends React.Component<
        IInvertableListProps & { intl: IntlShape },
        IInvertableListState
    > {}
    export default class InvertableListIntl extends React.Component<
        IInvertableListProps,
        IInvertableListState
    > {}
}
