declare module "@gooddata/goodstrap/lib/List/MultiSelectListItem" {
    import { IntlShape } from "react-intl";

    export interface IMultiSelectListItemSource {
        title: string;
    }

    export interface IMultiSelectListItemProps {
        intl: IntlShape;
        isLoading: boolean;
        onMouseOut: (source: IMultiSelectListItemSource) => void;
        onMouseOver: (source: IMultiSelectListItemSource) => void;
        onOnly: (source: IMultiSelectListItemSource) => void;
        onSelect: (source: IMultiSelectListItemSource) => void;
        selected: boolean;
        source: IMultiSelectListItemSource;
    }

    export class MultiSelectListItem extends React.Component<
        IMultiSelectListItemProps & { intl: IntlShape }
    > {
        handleSelect: () => void;
        handleMouseOver: () => void;
        handleMouseOut: () => void;
        renderOnly: () => void;
    }
    export default class MultiSelectListItemIntl extends React.Component<IMultiSelectListItemProps> {}
}
