/**
 * Checks if hostname belongs to production server.
 * @param {String} hostname hostname to check
 * @returns {Boolean} whether hostname is production or not
 */
export function isProductionHostname(hostname: string): boolean {
    return !(hostname === 'localhost'
        || /getgooddata\.com$/.test(hostname)
        || /intgdc\.com$/.test(hostname)
    );
}
