import {
    isProductionHostname
} from '../env';

describe('#isProductionHostname', () => {
    it('should return true for known secures', () => {
        expect(isProductionHostname('secure.gooddata.com')).toBeDefined();
        expect(isProductionHostname('na1.secure.gooddata.com')).toBeDefined();
        expect(isProductionHostname('secure.eu.gooddata.com')).toBeDefined();
    });

    it('should return false for known non-secures', () => {
        expect(isProductionHostname('staging.getgooddata.com')).toBeFalsy();
        expect(isProductionHostname('staging.intgdc.com')).toBeFalsy();
        expect(isProductionHostname('staging2.getgooddata.com')).toBeFalsy();
        expect(isProductionHostname('staging2.intgdc.com')).toBeFalsy();
        expect(isProductionHostname('staging3.getgooddata.com')).toBeFalsy();
        expect(isProductionHostname('staging3.intgdc.com')).toBeFalsy();
        expect(isProductionHostname('localhost')).toBeFalsy();
    });
});
