import { getUserId } from '../userUtils';

describe('getUserId', () => {
    it('should return user identifier', () => {
        const bootStrapData = {
            bootstrapResource: {
                current: {
                    featureFlags: {}
                },
                accountSetting: {
                    links: {
                        self: '/gdc/profile/userId123'
                    }
                }
            }
        }
        const userId = getUserId(bootStrapData);
        expect(userId).toBe('userId123');
    });

    it('should return empty string when no user in bootstrap data', () => {
        const bootStrapData = {
            bootstrapResource: {
                current: {
                    featureFlags: {}
                },
                accountSetting: {
                    links: {
                        self: ''
                    }
                }
            }
        }
        const userId = getUserId(bootStrapData);
        expect(userId).toBe('');
    })
})
