export function removeMetadata(translationsWithMetadata: any) {
    const translations = {};
    Object.keys(translationsWithMetadata).forEach((key) => {
        translations[key] = typeof translationsWithMetadata[key] === 'object'
            ? translationsWithMetadata[key].value
            : translationsWithMetadata[key];
    });
    return translations;
}
